import React from 'react';
import Image from '../assets/avatar.svg';
import { TypeAnimation } from 'react-type-animation';
import { FaGithub, FaYoutube, FaDribbble } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { fadeIn } from '../variants';

const Banner = () => {
  return (
    <section className="min-h-[85vh] lg:min-h-[78vh] flex items-center" id='home'>
      <div className='container mx-auto'>
        <div className='flex flex-col gap-y-8 lg:flex-row lg:items-center lg:gap-x-12'>
          <div className='flex-1 text-center font-secondary lg:text-left'>
            <motion.h1 
            variants={fadeIn('up',0.3)}
            initial='hidden'
            whileInView={'show'}
            viewport={{once:false, amount:0.7}}
            className='text-[45px] pl-32 lg:p font-bold leading-[0.8] lg:text-[60px]'>
              SIFEN <span>TESFA</span>
            </motion.h1>
            <motion.div variants={fadeIn('up',0.3)}
            initial='hidden'
            whileInView={'show'}
            viewport={{once:false, amount:0.7}} className='mb-6 text-[36px] lg:text-[40px] font-secondary font-semibold uppercase leading-[1]'>
              <span className='text-white mr-4'>I am a</span>
              <TypeAnimation
                sequence={[
                  "Developer", 2000,
                  "Deve", 2000,
                  "Developer", 2000,
                ]}
                speed={50}
                className='text-accent'
                wrapper='span'
                repeat={Infinity}
              />
            </motion.div>
            <motion.p variants={fadeIn('up',0.3)}
            initial='hidden'
            whileInView={'show'}
            viewport={{once:false, amount:0.7}} className='mb-8 max-w-lg mx-auto lg-mx-0'>
              I’m a front-end web developer specializing in building (and occasionally designing) exceptional digital experiences.
              Currently, I’m focused on building responsive front-end web applications.
            </motion.p>
            <motion.div variants={fadeIn('up',0.3)}
            initial='hidden'
            whileInView={'show'}
            viewport={{once:false, amount:0.7}}
            className='flex max-w-max gap-x-6 items-center mb-12 mx-auto lg:mx-0'>
              <button className='btn btn-lg'>Contact me</button>
              <a href='#' className='text-gradient btn-link'> My Portfolio</a>
            </motion.div>
            <motion.div
            variants={fadeIn('up',0.3)}
            initial='hidden'
            whileInView={'show'}
            viewport={{once:false, amount:0.7}} className='flex text-[20px] gap-x-6 max-w-max mx-auto'>
              <a href='#'>
                <FaYoutube />
              </a>
              <a href='#'>
                <FaGithub />
              </a>
              <a href='#'>
                <FaDribbble />
              </a>
            </motion.div>
          </div>
          <motion.div
          variants={fadeIn('down',0.5)} 
          initial='hidden'
          whileInView={'show'}
          viewport={{once:false, amount:0.7}}
          className='hidden lg:flex flex-1 max-w-[200px] lg:max-w-[342px]'>
            <img src={Image} alt='' />
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
