import React from 'react';
import Lottie from 'react-lottie';
import load from './load.json';
import CountUp from "react-countup"
import { useInView } from 'react-intersection-observer'
import { motion } from 'framer-motion'
import { fadeIn } from '../variants'

const About = () => {
  const [ref, inView] = useInView({
    threshold: 0.5,
  });

  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: load,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice',
    },
  };

  return (
    <section className="section" id='about'>
      <div className='container mx-auto'>
        <div className='flex justify-center'>
          <div className='lottie-container'>
            <Lottie options={defaultOptions} width={400} />
          </div>
        </div>
        <div>
          <h2>About me.</h2>
          <h3>Hi. I'm Sifen Tesfa, nice to meet you. Please take a look around.</h3>
          <p>
            I am passionate about building excellent software that improves the lives of those around me. I specialize
            in creating software for clients ranging from individuals and small businesses all the way to large enterprise corporations.
            What would you do if you had a software expert available at your fingertips?
          </p>
          <div className='flex'>
            <div>
              <div className='text-[40px] font-tertiary text-gradient mb-2'>
                {inView ? <Countup start={0} end={13} duration={3} /> : null}
                <Countup start={0} end={13} duration={3} />
              </div>
              <div className='font-primary text-sm tracking-[2px]'>
                Years of <br />
                Experience
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
